SELECT HISTORY_ID, CAR_ID, date_format(start_date,'%Y-%m-%d') as START_DATE, date_format(end_date,'%Y-%m-%d') as END_DATE, if(to_days(end_date) - to_days(start_date)>=29, '장기 대여', '단기 대여') AS RENT_TYPE  from CAR_RENTAL_COMPANY_RENTAL_HISTORY where (month(start_date)=9 and year(start_date)=2022) order by HISTORY_ID desc;